import React from "react"

class Game extends React.Component{
    constructor() {
        super();
        this.state = {
            order: first(),
            board: [" ", " ", " ", " ", " ", " ", " ", " ", " "],
            winner: null,
            full: false,
            current: this.state.order[0],
            playing: true,
        };
    }

    first() {
        let index = Math.round(Math.random)
        if (index === 0) {
            return(["X", "0"])
        } else {
            return(["0", "X"])
        }

    }

    placeMarker(position) {
        let currentBoard = this.state.board
        if (this.space_check(position-1)) {
            currentBoard[position - 1] = this.state.current
            if (this.state.current === "X") {
                let newCurrent = "0"
            } else {
                let newCurrent = "X"
            }
            this.setState({
                board: currentBoard, 
                current: newCurrent
            })
        }
    }

    displayBoard(board) {
        if (this.state.winner != null) {
            return(
                <div className="text-center m-auto">
                    <p class="text-5xl text-indigo-900 font-bold font-mono text-center mb-20">Congratulations {this.state.winner[1]}!</p>
                </div>
            )
        } else if (this.state.full === false) {
            return(
                <div class="text-center m-auto">
                    <div class="flex justify-center">
                        <a class="border border-indigo-500 p-8 py-7 hover:bg-blue-100" onClick={this.placeMarker(7)}>{board[6]}</a>
                        <a class="border border-indigo-500 p-8 py-7 hover:bg-blue-100" onClick={this.placeMarker(8)}>{board[7]}</a>
                        <a class="border border-indigo-500 p-8 py-7 hover:bg-blue-100" onClick={this.placeMarker(9)}>{board[8]}</a>
                    </div>
                    <div class="flex justify-center">
                        <a class="border border-indigo-500 p-8 py-7 hover:bg-blue-100" onClick={this.placeMarker(4)}>{board[3]}</a>
                        <a class="border border-indigo-500 p-8 py-7 hover:bg-blue-100" onClick={this.placeMarker(5)}>{board[4]}</a>
                        <a class="border border-indigo-500 p-8 py-7 hover:bg-blue-100" onClick={this.placeMarker(6)}>{board[5]}</a>
                    </div>
                    <div class="flex justify-center">
                        <a class="border border-indigo-500 p-8 py-7 hover:bg-blue-100" onClick={this.placeMarker(1)}>{board[0]}</a>
                        <a class="border border-indigo-500 p-8 py-7 hover:bg-blue-100" onClick={this.placeMarker(2)}>{board[1]}</a>
                        <a class="border border-indigo-500 p-8 py-7 hover:bg-blue-100" onClick={this.placeMarker(3)}>{board[2]}</a>
                    </div>
                </div>
            )
        } else {
            return(
                <div className="text-center m-auto">
                    <p class="text-5xl text-indigo-900 font-bold font-mono text-center mb-20">The board is full. This results in a tie.</p>
                </div>
            )
        }

    }

    winCheck(board, marker) {
        let winning = ["159", "357", "123", "456", "789", "147", "258", "369"]
        let won = false

        for (const solution of winning) {
            if (board[int(solution[0])-1] === board[int(solution[1])-1] && board[int(solution[1])-1] === board[int(solution[2])-1] && board[int(solution[1])-1] != " "){
                console.log(marker + " wins! Congratulations!")
                won = true
            }
        }
        if (won === true) {
            this.setState({
                winner:[won, marker]
            })
        }

    }

    space_check(position){
        if (this.state.board[position-1] == "X" || this.state.board[position-1] == "O"){
            return false
        } else {
            return true
        }
    }

    full_board_check() {
        let full = true
        for (const position in this.state.board){
            if (this.state.board[position] === " ") {
                full = false
            }
        }

        this.setState({
            full: full,
        })
    }

    render () {
        return(
            <div>
                <Link class="bg-red-600 text-white p-10 py-2 rounded mt-8 border-2 border-red-600 hover:bg-white hover:text-red-600 object-right mx-8">Quit</Link>
                {displayBoard(this.state.board)}
            </div>
        )
    }
}

export default Game
