import React from "react"
import { useHistory, Link } from "react-router-dom";

class Game extends React.Component{

    first() {
        let index = Math.random() >= 0.5
        if (index === true) {
            return(["X", "0"])
        } else {
            return(["0", "X"])
        }

    }

    placeMarker(position) {
        console.log("placing marker")
        let currentBoard = this.state.board
        if (this.space_check(position)) {
            console.log("space is free")
            currentBoard[position - 1] = this.state.current
            if (this.state.current === "X") {
                let newCurrent = "0"

                this.winCheck("X")
                this.full_board_check()
                this.setState({
                    board: currentBoard, 
                    current: newCurrent
                })

            } else {
                let newCurrent = "X"
                this.winCheck("0")
                this.full_board_check()
                this.setState({
                    board: currentBoard, 
                    current: newCurrent
                })
            }
            console.log(currentBoard)
        }
    }

    displayBoard() {
        if (this.state.order === null) {
            let order = this.first()
            this.setState({
                order: order,
                current:order[0]
            })
        }
        if (this.state.winner !== null) {
            console.log("winner +" + this.state.winner.toString())
            return(
                <div className="text-center m-auto">
                    <p class="text-5xl text-indigo-900 font-bold font-mono text-center mb-20">Congratulations {this.state.winner[1]}!</p>
                </div>
            )
        } else if (this.state.full === false) {
            console.log("you can keep playing")
            return(
                <div class="text-center m-auto">
                    <div class="flex justify-center">
                        <a class={"border border-indigo-500 p-8 hover:bg-blue-100" + (this.space_check(7) ? 'p-10 px-10' : 'px-8 py-5')} onClick={() => this.placeMarker(7)}>{this.state.board[6]}</a>
                        <a class={"border border-indigo-500 p-8 hover:bg-blue-100" + (this.space_check(8) ? 'p-10 px-10' : 'px-8 py-5')} onClick={() => this.placeMarker(8)}>{this.state.board[7]}</a>
                        <a class={"border border-indigo-500 p-8 hover:bg-blue-100" + (this.space_check(9) ? 'p-10 px-10' : 'p-8 py-7')} onClick={() => this.placeMarker(9)}>{this.state.board[8]}</a>
                    </div>
                    <div class="flex justify-center">
                        <a class={"border border-indigo-500 p-8 hover:bg-blue-100" + (this.space_check(4) ? 'p-10 px-10' : 'p-8 py-7')} onClick={() => this.placeMarker(4)}>{this.state.board[3]}</a>
                        <a class={"border border-indigo-500 p-8 hover:bg-blue-100" + (this.space_check(5) ? 'p-10 px-10' : 'p-8 py-7')} onClick={() => this.placeMarker(5)}>{this.state.board[4]}</a>
                        <a class={"border border-indigo-500 p-8 hover:bg-blue-100" + (this.space_check(6) ? 'p-10 px-10' : 'p-8 py-7')} onClick={() => this.placeMarker(6)}>{this.state.board[5]}</a>
                    </div>
                    <div class="flex justify-center">
                        <a class={"border border-indigo-500 p-8 hover:bg-blue-100" + (this.space_check(1) ? 'p-10 px-10' : 'p-8 py-7')} onClick={() => this.placeMarker(1)}>{this.state.board[0]}</a>
                        <a class={"border border-indigo-500 p-8 hover:bg-blue-100" + (this.space_check(2) ? 'p-10 px-10' : 'p-8 py-7')} onClick={() => this.placeMarker(2)}>{this.state.board[1]}</a>
                        <a class={"border border-indigo-500 p-8 hover:bg-blue-100" + (this.space_check(3) ? 'p-10 px-10' : 'p-8 py-7')} onClick={() => this.placeMarker(3)}>{this.state.board[2]}</a>
                    </div>
                </div>
            )
        } else {
            return(
                <div className="text-center m-auto">
                    <p class="text-5xl text-indigo-900 font-bold font-mono text-center mb-20">The this.state.board is full. This results in a tie.</p>
                </div>
            )
        }

    }

    winCheck(marker) {
        let winning = ["159", "357", "123", "456", "789", "147", "258", "369"]
        let won = false

        for (const solution of winning) {
            if (this.state.board[parseInt(solution[0])-1] === this.state.board[parseInt(solution[1])-1] && this.state.board[parseInt(solution[1])-1] === this.state.board[parseInt(solution[2])-1] && this.state.board[parseInt(solution[1])-1] != " "){
                console.log(marker + " wins! Congratulations!")
                won = true
            }
        }
        if (won === true) {
            this.setState({
                winner:[won, marker]
            })
        }

    }

    space_check(position){
        if (this.state.board[position-1] == "X" || this.state.board[position-1] == "O"){
            return false
        } else {
            return true
        }
    }

    full_board_check() {
        let full = true
        for (const position in this.state.board){
            if (this.state.board[position] === " ") {
                full = false
            }
        }

        this.setState({
            full: full,
        })
    }

    constructor() {
        super();
        this.state = {
            order: null,
            board: [" ", " ", " ", " ", " ", " ", " ", " ", " "],
            winner: null,
            full: false,
            current: null,
            playing: true,
        };
    }


    render () {
        return(
            <div class="mt-8 flex-col justify-center">
                <Link exact to="/" class="bg-red-600 text-white p-10 py-2 rounded mt-8 border-2 border-red-600 hover:bg-white hover:text-red-600 object-right mx-8">Quit</Link>
                {this.displayBoard(this.state.board)}
            </div>
        )
    }
}

export default Game
